($result);                      }                      $no = 1;                      $data = mysqli_query($koneksi, "SELECT *FROM customer");                      while ($row = mysqli_fetch_array($data)) {                        echo "<tr>                        <td>".$no."</td>                        <td>".$row['name']."</td>                        <td>".$row['telp']."</td>                        <td>".$row['email']."</td>                        <td>".$row['sosmed']."</td>                                                <td>".$row['tkp']."</td>                        <td>".$row['bill']."</td>                        <td>".$row['is_order']."</td>                        <td>". '<img src="foto/$row[foto]" width="50">' ."</td>                        <td>".$row['created_at']."</td>                        </tr>";                        $no++;                      }                      ?>                    </tbody>                  </table>                <!-- </div> -->              </div>              <!-- /.card-body -->            </div>          </div>          <!-- /.col -->        </div>        <!-- /.row -->      </div>      <!-- /.container-fluid -->    </section>  </div></div><!-- ./wrapper --></body></html>